import React, { useContext, Suspense, useState, useEffect } from 'react';
import { Route, Switch, Redirect } from 'react-router-dom';

import Layout from './components/layout/Layout';
import LoadingSpinner from './components/UI/LoadingSpinner';
import AdminPage from './pages/AdminPage';
import AuthContext from './store/auth-context';

function App() {
  // const QuoteDetails = React.lazy(() => import('./pages/QuoteDetails'));
  // const NewQuote = React.lazy(() => import('./pages/NewQuote'));
  // const AllQuotes = React.lazy(() => import('./pages/AllQuotes'));
  const NotFound = React.lazy(() => import('./pages/NotFound'));
  const ProfilePage = React.lazy(() => import('./pages/ProfilePage'));
  const AuthPage = React.lazy(() => import('./pages/AuthPage'));
  const Rent = React.lazy(() => import('./pages/Rent'));

  const authCtx = useContext(AuthContext);

  const [isAdmin, setIsAdmin] = useState(false);
  const loggedIn = authCtx.isLoggedIn;
  const data = authCtx.data;

  useEffect(() => {
    if (authCtx.role === 'admin') {
      setIsAdmin(true);
    } else {
      setIsAdmin(false);
    }
  }, [isAdin]);

  return (
    <Layout>
      <Suspense
        fallback={
          <div className="centered">
            <LoadingSpinner />
          </div>
        }
      >
        <Switch>
          <Route path="/" exact>
            {loggedIn && <Redirect to="/mainPage" />}
            {!loggedIn && <Redirect to="/auth" />}
          </Route>
          <Route path="/auth">
            {loggedIn && <Redirect to="/mainPage" />}
            {!loggedIn && <AuthPage />}
          </Route>
          <Route path="/mainPage">
            {loggedIn && <ProfilePage user={data} />}
            {!loggedIn && <Redirect to="/auth" />}
          </Route>
          <Route path="/rent">
            {loggedIn && <Rent user={data} />}
            {!loggedIn && <Redirect to="/auth" />}
          </Route>
          <Route path="/admin">
            {loggedIn && isAdmin && <AdminPage user={data} />}
            {loggedIn && !isAdmin && <Redirect to="/auth" />}
            {!loggedIn && !isAdmin && <Redirect to="/auth" />}
          </Route>
          <Route path="*">
            <NotFound />
          </Route>
        </Switch>
      </Suspense>
    </Layout>
  );
}

export default App;
