import React, { useContext, Suspense, useState, useEffect } from 'react';
import { Route, Switch, Redirect, useHistory } from 'react-router-dom';

import Layout from './components/layout/Layout';
import LoadingSpinner from './components/UI/LoadingSpinner';
import AdminPage from './pages/AdminPage';
import Cycles from './pages/Cycles';
import AuthContext from './store/auth-context';
// import useHttp from './components/hooks/use-http';
// import { checkUser } from './components/lib/api';

function App() {
  const NotFound = React.lazy(() => import('./pages/NotFound'));
  const ProfilePage = React.lazy(() => import('./pages/ProfilePage'));
  const AuthPage = React.lazy(() => import('./pages/AuthPage'));
  const Rent = React.lazy(() => import('./pages/Rent'));
  const Signup = React.lazy(() => import('./pages/Signup'));

  const authCtx = useContext(AuthContext);
  const history = useHistory();

  let { sendRequest, status, data: cycle, error } = useHttp(checkUser, false);

  const [isAdmin, setIsAdmin] = useState(false);
  const loggedIn = authCtx.isLoggedIn;
  const data = authCtx.data;
  useEffect(() => {
    if (loggedIn) {
      sendRequest({
        userid: authCtx.userid,
      });
      authCtx.login(data, data.token, data._id, data.role, data.cycleid);
    }
  });

  useEffect(() => {
    if (authCtx.role === 'admin') {
      setIsAdmin(true);
    } else {
      setIsAdmin(false);
    }
  }, [isAdmin, authCtx.role, authCtx]);

  return (
    <Layout>
      <Suspense
        fallback={
          <div className="centered">
            <LoadingSpinner />
          </div>
        }
      >
        <Switch>
          <Route path="/" exact>
            {loggedIn && <Redirect to="/mainPage" />}
            {!loggedIn && <Redirect to="/auth" />}
          </Route>
          <Route path="/signup">
            {loggedIn && <Redirect to="/mainPage" />}
            {!loggedIn && <Signup />}
          </Route>
          <Route path="/auth">
            {loggedIn && <Redirect to="/mainPage" />}
            {!loggedIn && <AuthPage />}
          </Route>
          <Route path="/mainPage">
            {loggedIn && <ProfilePage user={data} />}
            {!loggedIn && <Redirect to="/auth" />}
          </Route>
          <Route path="/rent">
            {loggedIn && <Rent user={data} />}
            {!loggedIn && <Redirect to="/auth" />}
          </Route>
          <Route path="/admin">
            {loggedIn && isAdmin && <AdminPage user={data} />}
            {loggedIn && !isAdmin && <Redirect to="/auth" />}
            {!loggedIn && !isAdmin && <Redirect to="/auth" />}
          </Route>
          <Route path="/cycles">
            <Cycles />
          </Route>
          <Route path="*">
            <NotFound />
          </Route>
        </Switch>
      </Suspense>
    </Layout>
  );
}

export default App;
