import { useContext, useState } from 'react';
import Scanner from '../components/QrScaner/Scanner';
import { rentCycle } from '../components/lib/api';
import useHttp from '../components/hooks/use-http';
import Button from '../components/UI/Button';
import AuthContext from '../store/auth-context';
import LoadingSpinner from '../components/UI/LoadingSpinner';

const Rent = (props) => {
  const authCtx = useContext(AuthContext);
  const [data, setData] = useState(null);
  const [done, setDone] = useState(false);
  const role = '';
  const [one, setOne] = useState(false);
  const [two, setTwo] = useState(false);

  //   console.log(authCtx);
  const rentHandler = () => {
    let { sendRequest, status, data: cycle, error } = useHttp(rentCycle, true);
    sendRequest({ cycleid: data, userid: authCtx.userid, role: authCtx.role });
    if (status === 'pending') {
      return (
        <div className="centered">
          <LoadingSpinner />
        </div>
      );
    }
    if (error) {
      return <p className="centered">{error}</p>;
    }
    console.log(data);
  };

  if (role === 'admin') {
    setOne(true);
  }
  if (role === 'guard') {
    setTwo(true);
  }

  const ScanComplete = (props) => {
    setData(props);
    setDone(true);
  };
  return (
    <div>
      {!one && !two && (
        <div>
          {!done && <Scanner scan={ScanComplete} />}
          {data && <p>{data}</p>}
          {done && <Button click={rentHandler}>Rent</Button>}
        </div>
      )}
      {!one && two && (
        <div>
          {!done && <Scanner scan={ScanComplete} />}
          {data && <p>{data}</p>}
          {done && <Button click={rentHandler}>Return</Button>}
        </div>
      )}
      {one && !two && (
        <div>
          {!done && <Scanner scan={ScanComplete} />}
          {data && <p>{data}</p>}
          {done && <Button click={rentHandler}>Admin</Button>}
        </div>
      )}
    </div>
  );
};

export default Rent;
