import { Fragment } from "react";
import { useParams, Route, NavLink, useRouteMatch } from "react-router-dom";

import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";

const DUMMY_QUOTES = [
  {
    id: "q1",
    author: "Shivam",
    text: "Whoso neglects learning in his youth, losses the past and is dead for the future",
  },
  {
    id: "q2",
    author: "Jitendra",
    text: "Build in Private",
  },
  {
    id: "q3",
    author: "Shivam",
    text: "Everyone is working to be seen, i am working to disappear",
  },
];

const QuoteDetails = () => {
  const params = useParams();

  console.log(params);

  const quote = DUMMY_QUOTES.find((quote) => quote.id === params.quoteId);
  console.log(quote);
  if (!quote) {
    return <p>No Quote Found</p>;
  }

  return (
    <Fragment>
      <HighlightedQuote text={quote.text} author={quote.author} />
      <div className="centered">
        <Route path={`/quotes/${params.quoteId}`} exact>
          <NavLink
            className="btn--flat"
            to={`/quotes/${params.quoteId}/comments`}
          >
            Load Comments
          </NavLink>
        </Route>
      </div>
      <Route path={`/quotes/${params.quoteId}/comments/`}>
        <Comments />
        <div className="centered">
          <NavLink className="btn--flat" to={`/quotes/${params.quoteId}`}>
            Hide Comments
          </NavLink>
        </div>
      </Route>
    </Fragment>
  );
};

export default QuoteDetails;
