import { useState } from 'react';
import Button from '../UI/Button';
import classes from './AddStudentForm.module.css';
const Form = (props) => {
  const [addcy, setAddcy] = useState(false);
  const [delcy, setDelcy] = useState(false);
  const [addst, setAddst] = useState(false);
  const [delst, setDelst] = useState(false);
  const [addgu, setAddgu] = useState(false);
  const [delgu, setDelgu] = useState(false);

  const adds = () => {
    if (addst) setAddst(false);
    if (!addst) {
      setAddst(true);
      setDelst(false);
      setAddgu(false);
      setDelgu(false);
      setAddcy(false);
      setDelcy(false);
    }
  };
  const dels = () => {
    if (delst) setDelst(false);
    if (!delst) {
      setAddst(false);
      setDelst(true);
      setAddgu(false);
      setDelgu(false);
      setAddcy(false);
      setDelcy(false);
    }
  };
  const addg = () => {
    if (addgu) setAddgu(false);
    if (!addgu) {
      setAddst(false);
      setDelst(false);
      setAddgu(true);
      setDelgu(false);
      setAddcy(false);
      setDelcy(false);
    }
  };
  const delg = () => {
    if (delgu) setDelgu(false);
    if (!delgu) {
      setAddst(false);
      setDelst(false);
      setAddgu(false);
      setDelgu(true);
      setAddcy(false);
      setDelcy(false);
    }
  };
  const addc = () => {
    if (addcy) setAddcy(false);
    if (!addcy) {
      setAddst(false);
      setDelst(false);
      setAddgu(false);
      setDelgu(false);
      setAddcy(true);
      setDelcy(false);
    }
  };
  const delc = () => {
    if (delcy) setDelcy(false);
    if (!delcy) {
      setAddst(false);
      setDelst(false);
      setAddgu(false);
      setDelgu(false);
      setAddcy(false);
      setDelcy(true);
    }
  };

  return (
    <section>
      <form>
        <div className="centered">
          <Button click={props.printadds}>
            {addst ? 'Remove Add Student Form' : 'Show Add Student Form'}
          </Button>
          <Button click={props.printdels} toggle={adds}>
            {delst ? 'Remove Delete Student Form' : 'Show Delete Student Form'}
          </Button>
        </div>
        <div className="centered">
          <Button click={props.printaddg}>
            {addgu ? 'Remove Add Guard Form' : 'Show Add Guard Form'}
          </Button>
          <Button click={props.printdelg}>
            {delgu ? 'Remove Delete Guard Form' : 'Show Delete Guard Form'}
          </Button>
        </div>
        <div className="centered">
          <Button click={props.printaddc}>
            {addcy ? 'Remove Add Cycle Form' : 'Show Add Cycle Form'}
          </Button>
          <Button click={props.printdelc}>
            {delcy ? 'Remove Delete Cycle Form' : 'Show Delete Cycle Form'}
          </Button>
        </div>
      </form>
    </section>
  );
};

export default Form;
