import { useEffect, useHistory } from "react";

import useHttp from "../hooks/use-http";
import { deleteQuote } from "../lib/api";

const DeleteQuote = (props) => {
  //   const { sendRequest, status } = useHttp(deleteQuote);
  //   const history = useHistory();

  //   useEffect(() => {
  //     if (status === "completed") {
  //       history.push("/quotes");
  //     }
  //   }, [status, history]);

  const finalQuotes = props.quotes.filter((quote) => quote.id !== props.id);

  const deleteQuoteHandler = (quotes) => {
    // sendRequest(quotes);
    fetch("https://quotes-33a21-default-rtdb.firebaseio.com", {
      method: "POST",
      body: JSON.stringify(quotes),
      headers: {
        "Content-Type": "application/json",
      },
    });
  };

  deleteQuoteHandler(finalQuotes);
};

export default DeleteQuote;
