import { Fragment } from "react";
import { useParams, Route, NavLink } from "react-router-dom";

import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";

const DUMMY_QUOTES = [
  {
    id: "q1",
    author: "Shivam",
    text: "Whoso neglects learning in his youth, losses the past and is dead for the future",
  },
  {
    id: "q2",
    author: "Jitendra",
    text: "Build in Private",
  },
  {
    id: "q3",
    author: "Shivam",
    text: "Everyone is working to be seen, i am working to disappear",
  },
];

const QuoteDetails = () => {
  const params = useParams();
  console.log(params);

  const quote = DUMMY_QUOTES.find((quote) => quote.id === params.id);
  console.log(quote);

  return (
    <Fragment>
      {/* <HighlightedQuote text={quote.text} auther={quote.author} /> */}
      <NavLink to={`/quotes/${params.quoteId}/comments/`}>Comments</NavLink>
      <Route path={`/quotes/${params.quoteId}/comments/`}>
        <Comments />
      </Route>
    </Fragment>
  );
};

export default QuoteDetails;
