import React, { useState } from "react";

const QuotesContext = React.createContext({
  quote: {},
  refresh: (quotes) => {},
});

export const QuotesContextProvider = (props) => {
  const [quotes, setQuotes] = useState(null);

  const getQuotes = (newQuotes) => {
    setQuotes(newQuotes);
  };

  const contextValue = {
    quote: quotes,
    refresh: getQuotes,
  };

  return (
    <QuotesContext.Provider value={contextValue}>
      {props.children}
    </QuotesContext.Provider>
  );
};

export default QuotesContext;
