import { useEffect, useState } from 'react';

import AddCycle from '../components/Admin/AddCycle';
import AddGuard from '../components/Admin/AddGuard';
import AddStudentForm from '../components/Admin/AddStudentForm';
import DeleteCycle from '../components/Admin/DeleteCycle';
import DeleteGuard from '../components/Admin/DeleteGuard';
import DeleteStudent from '../components/Admin/DeleteStudent';
import Form from '../components/Admin/Form';

const AdminPage = () => {
  const [addst, setAddst] = useState(false);
  const [delst, setDelst] = useState(false);
  const [addgu, setAddgu] = useState(false);
  const [delgu, setDelgu] = useState(false);
  const [addcy, setAddcy] = useState(false);
  const [delcy, setDelcy] = useState(false);
  const [showForm, setShowForm] = useState(true);

  const adds = () => {
    if (addst) setAddst(false);
    if (!addst) {
      setAddst(true);
      setDelst(false);
      setAddgu(false);
      setDelgu(false);
      setAddcy(false);
      setDelcy(false);
    }
  };
  const dels = () => {
    if (delst) setDelst(false);
    if (!delst) {
      setAddst(false);
      setDelst(true);
      setAddgu(false);
      setDelgu(false);
      setAddcy(false);
      setDelcy(false);
    }
  };
  const addg = () => {
    if (addgu) setAddgu(false);
    if (!addgu) {
      setAddst(false);
      setDelst(false);
      setAddgu(true);
      setDelgu(false);
      setAddcy(false);
      setDelcy(false);
    }
  };
  const delg = () => {
    if (delgu) setDelgu(false);
    if (!delgu) {
      setAddst(false);
      setDelst(false);
      setAddgu(false);
      setDelgu(true);
      setAddcy(false);
      setDelcy(false);
    }
  };
  const addc = () => {
    if (addcy) setAddcy(false);
    if (!addcy) {
      setAddst(false);
      setDelst(false);
      setAddgu(false);
      setDelgu(false);
      setAddcy(true);
      setDelcy(false);
    }
  };
  const delc = () => {
    if (delcy) setDelcy(false);
    if (!delcy) {
      setAddst(false);
      setDelst(false);
      setAddgu(false);
      setDelgu(false);
      setAddcy(false);
      setDelcy(true);
    }
  };

  let states;
  useEffect(() => {
    states = {
      addst,
      delst,
      addgu,
      delgu,
      addcy,
      delcy,
    };
  }, [addst, delst, addcy, delcy, addgu, delgu]);

  return (
    <div>
      {addst && <AddStudentForm />}
      {delst && <DeleteStudent />}
      {addgu && <AddGuard />}
      {delgu && <DeleteGuard />}
      {addcy && <AddCycle />}
      {delcy && <DeleteCycle />}
      {
        <Form
          states={states}
          printadds={adds}
          printdels={dels}
          printaddg={addg}
          printdelg={delg}
          printaddc={addc}
          printdelc={delc}
        />
      }
    </div>
  );
};
export default AdminPage;
