const url = 'https://pacific-fortress-54764.herokuapp.com';

export async function rentCycle(props) {
  // const authCtx = useContext(AuthContext);
  // console.log({
  //   'Content-Type': 'application/json',
  //   Authorization: 'Bearer ' + props,
  // });
  // console.log(props);
  let response = await fetch(`${url}/cycles/${props.cycleid}`, {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      // Authorization: 'Bearer ' + props,
    },
  });
  let data = await response.json();

  if (!response.ok) {
    // console.log(response);
    throw new Error(data.message || 'Could not fetch quotes.');
  }

  if (props.role === 'student' && data.status === 'rented') {
    throw new Error('Cycle already Rented');
  }

  if (props.role === 'guard' && data.status === '') {
    throw new Error('Cycle not Rented Yet');
  }

  let cycleid;
  if (props.role === 'student') cycleid = props.cycleid;
  if (props.role === 'guard') cycleid = '';

  // console.log({
  //   cycleid,
  //   userid: props.userid,
  // });
  response = await fetch(`${url}/users/${props.userid}`, {
    method: 'PATCH',
    body: JSON.stringify({ cycleid }),
    headers: {
      'Content-Type': 'application/json',
      // Authorization: 'Bearer ' + props,
    },
  });

  data = await response.json();

  // console.log(response);
  if (!response.ok) {
    throw new Error(data.message || 'Could not fetch quotes.');
  }

  let user;
  if (props.role === 'student') user = props.userid;
  if (props.role === 'guard') user = '';

  let stat;
  if (props.role === 'student') stat = 'rented';
  if (props.role === 'guard') stat = '';

  // console.log({
  //   staus: stat,
  //   userid: user,
  // });

  response = await fetch(`${url}/cycles/${props.cycleid}`, {
    method: 'PATCH',
    body: JSON.stringify({ status: stat, stdid: user }),
    headers: {
      'Content-Type': 'application/json',
      // Authorization: 'Bearer ' + props,
    },
  });

  data = await response.json();

  if (!response.ok) {
    // console.log(response);
    throw new Error(data.message || 'Could not fetch quotes.');
  }

  console.log('rented');
  return 'Cycle Rented';
}

