const url = 'https://pacific-fortress-54764.herokuapp.com';

export async function rentCycle(props) {
  // const authCtx = useContext(AuthContext);
  // console.log({
  //   'Content-Type': 'application/json',
  //   Authorization: 'Bearer ' + props,
  // });
  let response = await fetch(`${url}/cycles/${props.cycleid}`, {
    headers: {
      'Content-Type': 'application/json',
      Authorization: 'Bearer ' + props,
    },
  });
  let data = await response.json();

  if (!response.ok) {
    console.log(response);
    throw new Error(data.message || 'Could not fetch quotes.');
  }

  if (props.role === 'user' && data.status === 'rented') {
    throw new Error('Cycle already Rented');
  }

  if (props.role === 'guard' && data.status === '') {
    throw new Error('Cycle already Rented');
  }

  let cycle;
  if (props.role === 'user') cycle = props.cycleid;
  if (props.role === 'guard') cycle = '';

  console.log({
    cycleid: cycle,
  });

  response = await fetch(`${url}/users/${props.userid}`, {
    method: 'PATCH',
    body: JSON.stringify({ cycleid: cycle }),
    headers: {
      'Content-Type': 'application/json',
      Authorization: 'Bearer ' + props,
    },
  });

  data = await response.json();

  if (!response.ok) {
    console.log(response);
    throw new Error(data.message || 'Could not fetch quotes.');
  }

  let user;
  if (props.role === 'user') user = props.userid;
  if (props.role === 'guard') user = '';

  let stat;
  if (props.role === 'user') stat = 'rented';
  if (props.role === 'guard') stat = '';

  console.log({
    staus: stat,
    userid: user,
  });

  response = await fetch(`${url}/cycles/${props.userid}`, {
    method: 'PATCH',
    body: JSON.stringify({ status: stat, userid: user }),
    headers: {
      'Content-Type': 'application/json',
      Authorization: 'Bearer ' + props,
    },
  });

  data = await response.json();

  if (!response.ok) {
    console.log(response);
    throw new Error(data.message || 'Could not fetch quotes.');
  }
  return data;
}

