import { Fragment, useEffect, useState } from "react";
import {
  useParams,
  Route,
  NavLink,
  useRouteMatch,
  useHistory,
} from "react-router-dom";

import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";
import DeleteQuote from "../components/quotes/DeleteQuote";
import useHttp from "../components/hooks/use-http";
import LoadingSpinner from "../components/UI/LoadingSpinner";
import { getSingleQuote, deleteQuote } from "../components/lib/api";

const DeleteQuotes = (quote) => {
  const history = useHistory();
  const match = useRouteMatch();
  const params = useParams();
  const { quoteId } = params;
  const {
    sendRequest,
    status,
    data: loadedQuote,
    error,
  } = useHttp(deleteQuote, true);

  useEffect(() => {
    sendRequest(quoteId);
  }, [sendRequest, quoteId]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }

  if (error) {
    return <p className="centered">{error}</p>;
  }

  if (!loadedQuote.text) {
    return <p>No Quote Found</p>;
  }
};

const QuoteDetails = (props) => {
  const [isLoading, setIsLoading] = useState(false);

  const history = useHistory();
  const match = useRouteMatch();
  const params = useParams();
  const { quoteId } = params;
  const {
    sendRequest,
    status,
    data: loadedQuote,
    error,
  } = useHttp(getSingleQuote, true);

  useEffect(() => {
    sendRequest(quoteId);
  }, [sendRequest, quoteId]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }

  if (error) {
    return <p className="centered">{error}</p>;
  }

  if (!loadedQuote.text) {
    return <p>No Quote Found</p>;
  }

  const deletionHandler = () => {
    console.log("delete");
    const data = { id: loadedQuote.id };
    setIsLoading(true);
    DeleteQuote(data);
    setIsLoading(false);
    history.push("/");
  };
  // console.log(props.quotes);

  return (
    <Fragment>
      {isLoading && <LoadingSpinner />}
      <HighlightedQuote
        text={loadedQuote.text}
        author={loadedQuote.author}
        id={loadedQuote.id}
        deletionHandler={deletionHandler}
      />
      <div className="centered">
        <Route path={match.path} exact>
          <NavLink className="btn--flat" to={`${match.url}/comments`}>
            Load Comments
          </NavLink>
        </Route>
      </div>
      <Route path={`${match.path}/comments/`}>
        <Comments />
        <div className="centered">
          <NavLink className="btn--flat" to={`${match.url}`}>
            Hide Comments
          </NavLink>
        </div>
      </Route>
    </Fragment>
  );
};

export default QuoteDetails;
