import { Fragment, useEffect } from "react";
import {
  useParams,
  Route,
  NavLink,
  useRouteMatch,
  useHistory,
} from "react-router-dom";

import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";
// import DeleteQuote from "../components/quotes/DeleteQuote";
import NoQuotesFound from "../components/quotes/NoQuotesFound";
import useHttp from "../components/hooks/use-http";
import LoadingSpinner from "../components/UI/LoadingSpinner";
import { getSingleQuote } from "../components/lib/api";
import { getAllQuotes } from "../components/lib/api";
import { deleteQuote } from "../components/lib/api";

const DQuote = () => {
  const { sendRequest, status } = useHttp(deleteQuote);
  const history = useHistory();

  useEffect(() => {
    if (status === "completed") {
      history.push("/quotes");
    }
  }, [status, history]);

  const addQuoteHandler = (quoteData) => {
    sendRequest(quoteData);
  };
  return;
};

const GetQuotes = (props) => {
  const {
    sendRequest,
    status,
    data: loadedQuotes,
    error,
  } = useHttp(getAllQuotes, true);

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }

  if (error) {
    return <p className="centered">{error}</p>;
  }

  if (status === "completed" && (!loadedQuotes || loadedQuotes.length === 0)) {
    return <NoQuotesFound />;
  }

  if (status === "completed" && !loadedQuotes && loadedQuotes.length > 0) {
    const finalQuotes = loadedQuotes.filter((quote) => quote.id != props.id);
    addQuoteHandler();
  }
  return;
};

const QuoteDetails = () => {
  const match = useRouteMatch();
  const params = useParams();
  const { quoteId } = params;
  const {
    sendRequest,
    status,
    data: loadedQuote,
    error,
  } = useHttp(getSingleQuote, true);

  useEffect(() => {
    sendRequest(quoteId);
  }, [sendRequest, quoteId]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }

  if (error) {
    return <p className="centered">{error}</p>;
  }

  if (!loadedQuote.text) {
    return <p>No Quote Found</p>;
  }

  const deletionHandler = () => {
    console.log("delete");
    GetQuotes(loadedQuote.id);
    return (
      <div>
        <p>Delete</p>
        {/* <DeleteQuote id={loadedQuote.id} />; */}
      </div>
    );
  };

  return (
    <Fragment>
      <HighlightedQuote
        text={loadedQuote.text}
        author={loadedQuote.author}
        id={loadedQuote.id}
        deletionHandler={deletionHandler}
      />
      <div className="centered">
        <Route path={match.path} exact>
          <NavLink className="btn--flat" to={`${match.url}/comments`}>
            Load Comments
          </NavLink>
        </Route>
      </div>
      <Route path={`${match.path}/comments/`}>
        <Comments />
        <div className="centered">
          <NavLink className="btn--flat" to={`${match.url}`}>
            Hide Comments
          </NavLink>
        </div>
      </Route>
    </Fragment>
  );
};

export default QuoteDetails;
