import { Fragment, useState } from "react";
import { useEffect } from "react/cjs/react.development";

import QuoteList from "../components/quotes/QuoteList";
// import LoadingSpinner from "../components/UI/LoadingSpinner";
// import NoQuotesFound from "../components/quotes/NoQuotesFound";
// import useHttp from "../components/hooks/use-http";
// import { getAllQuotes } from "../components/lib/api";

const AllQuotes = (props) => {
  // const {
  //   sendRequest,
  //   status,
  //   data: loadedQuotes,
  //   error,
  // } = useHttp(getAllQuotes, true);

  // useEffect(() => {
  //   sendRequest();
  // }, [sendRequest]);

  // if (status === "pending") {
  //   return (
  //     <div className="centered">
  //       <LoadingSpinner />
  //     </div>
  //   );
  // }

  // if (error) {
  //   return <p className="centered">{error}</p>;
  // }

  // if (status === "completed" && (!loadedQuotes || loadedQuotes.length === 0)) {
  //   return <NoQuotesFound />;
  // }
  const [loadingDeleted, setloadingDeleted] = useState(false);
  const [httpError, setHttpError] = useState(false);

  const getDeleted = async () => {
    setloadingDeleted(true);
    const response = await fetch(
      "https://quotes-33a21-default-rtdb.firebaseio.com/deleted.json"
    );

    if (!response.ok) {
      throw new Error("Somethong Went Wrong");
    }

    const deleted = await response.json();
    // console.log("1");

    setloadingDeleted(false);
  };

  useEffect(() => {
    try {
      getDeleted();
    } catch {
      setloadingDeleted(false);
      setHttpError(true);
    }
    if (httpError) {
      return (
        <div className="centered">
          <h1>Could Not Fetch The data</h1>
        </div>
      );
    }
  }, [getDeleted, setloadingDeleted, setHttpError, httpError, loadingDeleted]);
  // console.log(deleted);
  // const quotes = props.quotes.filter(quote);

  return (
    <Fragment>
      <QuoteList quotes={props.quotes} />
    </Fragment>
  );
};

export default AllQuotes;
