import { useRef } from 'react';
import { useHistory } from 'react-router-dom';

import classes from './ProfileForm.module.css';

const ProfileForm = () => {
  const history = useHistory();
  const newPasswordRef = useRef();

  const submitionHandler = (event) => {
    event.preventDefault();

    const newEnteredPassword = newPasswordRef.current.value;

    fetch(
      'https://identitytoolkit.googleapis.com/v1/accounts:update?key=AIzaSyBzllPQgDfwwi_Q0dkj-5Luvef0kNOfy9M',
      {
        method: 'POST',
        body: JSON.stringify({
          password: newEnteredPassword,
          returnSecureToken: false,
        }),
        headers: {
          'Content-Type': 'application/json',
        },
      }
    ).then((res) => {
      history.replace('/');
    });
  };

  return (
    <form className={classes.form} onSubmit={submitionHandler}>
      <div className={classes.control}>
        <label htmlFor="new-password">New Password</label>
        <input
          type="password"
          id="new-password"
          minLength="7"
          ref={newPasswordRef}
        />
      </div>
      <div className={classes.action}>
        <button>Change Password</button>
      </div>
    </form>
  );
};

export default ProfileForm;
