import { useState, Fragment } from 'react';

import QRScan from 'qrscan';

const Scanner = (props) => {
  const [camera, setCamera] = useState(false);

  const openCamera = () => {
    setCamera(true);
    console.log(camera);
  };

  const onFind = (props) => {
    console.log(props);
    setCamera(false);
  };
  return (
    <div>
      {camera ? (
        <QRScan
          onFind={(result) => {
            if (result) props.scan;
          }}
        />
      ) : (
        <div>
          <button onClick={openCamera}>
            {camera ? 'Close Camera' : 'Open Camera'}
          </button>
        </div>
      )}
    </div>
  );
};

export default Scanner;
