import { useEffect } from "react";

import { deleteQuote } from "../lib/api";
import useHttp from "../hooks/use-http";
import LoadingSpinner from "../UI/LoadingSpinner";

const DeleteQuote = async (props) => {
  // const responce = await fetch(
  //   `https://quotes-33a21-default-rtdb.firebaseio.com/quotes/${props.id}.json`,
  //   {
  //     method: "DELETE",
  //     // body: JSON.stringify(props.id),
  //     headers: {
  //       "Content-Type": "application/json",
  //     },
  //   }
  // );

  // if (!responce.ok) {
  //   throw new Error("Could not detele quote.");
  // }

  const { sendRequest, status, error } = useHttp(deleteQuote, true);

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }

  if (error) {
    return <p className="centered">{error}</p>;
  }
};

export default DeleteQuote;
